import { useContext, useEffect, useState } from "react";
import { Context } from "./Context";
import { StyleSheet } from 'react-native';

const cssStyle = StyleSheet.create({
    header: {
        zIndex: 1,
    },
    text: {
        fontSize: 20,
    }
});

function LoginForm() {

    const openForm = useContext(Context);
    const [form, setOpenForm] = useState(openForm);
    console.log('###', form);
    let display_style: string = form ? "block" : "none";

    // useEffect(() => {
    //     display_style = form ? "none" : "block";
    //     console.log(display_style);
    // }, [form]);

    const block_display = {
        display_style: form ? "none" : "block"
    };

    return (
        <>

            <div style={[cssStyle.header, block_display]}>
                <form>
                    <label><b>Email</b></label>
                    <input type="text"></input>
                    <hr />
                    <label><b>Password</b></label>
                    <input type="passwprd"></input>
                    <hr />
                    <button type="submit">Login</button>
                    <button type="button" onClick={() => { setOpenForm({ form: false }); console.log(form); }}>Cancel</button>
                </form>
            </div>
        </>
    )

};

export default LoginForm;

function componentDidMount() {
    throw new Error("Function not implemented.");
}
