import { useContext, useEffect, useState } from "react";

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { Context } from "./Context";

function UserLogin() {

    const [user, setUser] = useState("Guest");
    const openForm = useContext(Context);
    const [form, setOpenForm] = useState(openForm);

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    return (
        <>
            <div className="d-flex flex-column flex-shrink-0 p-3 text-white bg-dark" style={{ width: 280 }}>
                <p>Hi {user}!</p>
                <p>Welcome to Access Workspace</p>
                {!show && <Button onClick={() => toggleShow(true)}>Show Toast</Button>}

                <Toast show={show} onClose={() => toggleShow(false)}>
                    <Toast.Header>
                        <img src="holder.js/20x20?text=%20" className="rounded mr-2" alt="" />
                    </Toast.Header>
                    <Toast.Body>
                        <p><label><b>Email</b></label></p>
                        <p><input type="text"></input></p>
                        <hr />
                        <p><label><b>Password</b></label></p>
                        <p><input type="password"></input></p>
                        <hr />
                    </Toast.Body>
                </Toast>
            </div>
        </>
    );
}

export default UserLogin;
