import { useContext, useEffect, useState } from "react";

import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { Context } from "./Context";

function UserLogin() {

    const [user, setUser] = useState("Guest");
    const openForm = useContext(Context);
    const [form, setOpenForm] = useState(openForm);

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
            <div className="d-flex flex-column flex-shrink-0 p-3 text-white bg-dark" style={{ width: 280, height: "100%", }}>
                <p>Hi {user}!</p>
                <p>Welcome to Access Workspace</p>
                <Button variant="primary" onClick={handleShow}>
                    Login
                </Button>

                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Login</Modal.Title>
                    </Modal.Header>
                    <Modal.Body><p><label><b>Email</b></label></p>
                        <p><input type="text"></input></p>
                        <p><label><b>Password</b></label></p>
                        <p><input type="password"></input></p>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" onClick={handleClose}>
                            Login
                        </Button>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
                </Modal>

            </div>
        </>
    );
}

export default UserLogin;
