import { useContext, useState } from "react";
import { Context } from "./Context";

function LoginForm() {

    const openForm = useContext(Context);
    const [form, setOpenForm] = useState(openForm);
    console.log('###', form);
    let display_style: string = form ? "none" : "block";

    function showLoginForm() {


        return (
            <>
                <div style={{ display: display_style, zIndex: 1, position: "fixed" }}>
                    <form>
                        <label><b>Email</b></label>
                        <input type="text"></input>
                        <hr />
                        <label><b>Password</b></label>
                        <input type="passwprd"></input>
                        <hr />
                        <button type="submit">Login</button>
                        <button type="button" onClick={() => { setOpenForm(false); console.log(form); }}>Cancel</button>
                    </form>
                </div>
            </>
        )
    }

    showLoginForm();
};

export default LoginForm;
